<?phprequire_once CORE.'/Db.php';class RightBar {       public static function getRightBar($type) {            $i = 0;      $postList = [];      $types = ['programms'=>'Программы','article'=>'Статии'];        while($row = $result->fetch()) {            $postList[$i]['id'] = $row['id'];            $postList[$i]['title'] = $row['title'];                        $postList[$i]['type'] = $types[$row['type']];            $i++;        }                //debug ($postList);                 require_once APP.'/views/layouts/rightbar.php';                  return $postList;                }}